{"method_body":"{
  if strict {
    if i < j && j < k {
      s := "increasing";
    } else if i > j && j > k {
      s := "decreasing";
    } else {
      s := "neither";
    }
  } else {
    if i <= j && j <= k {
      if i == j && j == k {
        s := "increasing and decreasing";
      } else {
        s := "increasing";
      }
    } else if i >= j && j >= k {
      s := "decreasing";
    } else {
      s := "neither";
    }
  }
}"}
