{"method_body":"{
  var res: seq<int> := [];
  var cur: int := 1;
  var i: int := 0;
  while i <= 20
    invariant 0 <= i <= 21
    invariant |res| == i
    invariant forall k :: 0 <= k < i ==> res[k] == (1 << k)
  {
    res := res + [cur];
    cur := cur * 2;
    i := i + 1;
  }
  powers := res;
}"}

