    invariant forall j :: 0 <= j < i ==> min <= inputs[j]
    invariant forall j :: 0 <= j < i ==> max >= inputs[j]
    invariant exists j :: 0 <= j < i && min == inputs[j]
    invariant exists j :: 0 <= j < i && max == inputs[j]
  {
