{"method_body":"{
  var res := "";
  var i := 0;
  while i < |s|
    invariant 0 <= i <= |s|
    invariant |res| == 2 * i
    invariant forall k :: 0 <= k < i ==> res[2*k] == s[k] && res[2*k+1] == '\n'
  {
    res := res + s[i] + "\n";
    i := i + 1;
  }
  chars := res;
}"}

