{"method_body":"{
  var res := [];
  var i := 0;
  while i < |arr|
    invariant 0 <= i <= |arr|
    invariant |res| <= i
    invariant forall k :: 0 <= k < |res| ==> res[k] % 2 == 0
    invariant forall k :: 0 <= k < i && arr[k] % 2 == 0 ==> (exists j :: 0 <= j < |res| && res[j] == arr[k])
  {
    if arr[i] % 2 == 0 {
      res := res + [arr[i]];
    }
    i := i + 1;
  }
  even_elements := res;
}"}

