{"method_body":"{
  var tmp := new int[|arr|];
  var j := 0;
  while j < |arr|
    invariant 0 <= j <= |arr|
    invariant forall k :: 0 <= k < j ==> tmp[k] == arr[|arr| - 1 - k]
  {
    tmp[j] := arr[|arr| - 1 - j];
    j := j + 1;
  }
  reversed := tmp[..];
}"}

