  var i := 1;
  while i < |arr| - 1
    invariant 1 <= i <= |arr| - 1
    invariant |s| == i
    invariant s[0] == arr[0]
    invariant forall k :: 1 <= k < i ==> s[k] == (if arr[k - 1] >= arr[k + 1] then arr[k - 1] else arr[k + 1])
  {
