  if |arr| % 2 == 1 {
    var mid := |arr| / 2;
    result := arr[..mid] + arr[mid+1..];
  } else {
    var mid1 := |arr| / 2 - 1;
    var mid2 := |arr| / 2;
    result := arr[..mid1] + arr[mid2+1..];
  }
}"}

