  var largest := arr[0];
  var second := arr[1];
  if second > largest {
    var tmp := largest;
    largest := second;
    second := tmp;
  }
  var i := 2;
  while i < |arr|
    invariant 0 <= i <= |arr|
    invariant forall j :: 0 <= j < i ==> arr[j] <= largest
    invariant forall j :: 0 <= j < i ==> (arr[j] < largest ==> arr[j] <= second)
    invariant second < largest
  {
    var x := arr[i];
    if x > largest {
      second := largest;
      largest := x;
    } else if x < largest && x > second {
      second := x;
    }
    i := i + 1;
  }
  second_largest := second;
}"}

