{"method_body":"{
  var i := 0;
  var sign := 1;
  alt_sum := 0;
  while i < |arr|
    invariant 0 <= i <= |arr|
    invariant alt_sum == sum_arrray(arr[..i], 1)
    invariant (sign == 1 && i % 2 == 0) || (sign == -1 && i % 2 == 1)
  {
    alt_sum := alt_sum + arr[i] * sign;
    sign := sign * -1;
    i := i + 1;
  }
}"}

